        title: '创建人',
        key: 'cName'
      }, {
        title: '创建时间',
        key: 'createTime'
      }, {
        title: '备注',
        key: 'remark'
      }, {
        title: '备注图片',
        key: 'rePic',
        render: function render(h, params) {
          if (!params.row.rePic) {
            return h('div', [h(clickImg, {
              props: {
                rePic: params.row.rePic
              }
