  props: {
    userId: {
      type: String,
      default: ''
    },
    memarkState: {
      type: Number,
      default: 1
    }
  },
  methods: {
    filtrateClick: function filtrateClick(e) {},
    getPageNum: function getPageNum(e) {
      this.pageNum = e;
    },
    cancel: function cancel() {},
    gitList: function gitList() {
      var _this = this;

      this.dataList = [];
      getPageCusRemark(this.pageNum, this.userId).then(function (res) {
        if (res.info === '暂无数据') {
          _this.$Message.error(res.info);

          _this.pageTotal = 1;
          return;
        }

        _this.pageTotal = res.info.pageTotal;
        res.info.data.map(function (item) {
          _this.dataList.push({
            cName: item.cName,
            createTime: item.createTime.split('.')[0].substring(0, 16),
            remark: item.remark,
            rePic: item.rePic.split(',')
