        });
      }).catch(function (err) {
        _this.$Message.error(err);
      });
    }
  },
  components: {
    editableTables: editableTables,
    clickImg: clickImg
  },
  mounted: function mounted() {
    this.gitList();
  },
  watch: {
    'memarkState': function memarkState(e) {
      this.pageNum = 1;
      this.gitList();
    }
  }
};
