import roleTableTree from '../cpmponents/roleTable-Tree';
export default {
  components: {
    roleTableTree: roleTableTree
  },
  data: function data() {
    return {
      modal: false,
      account: '',
      password: '',
      name: '',
      authority: '',
      modal_loading: false,
      newUserId: '',
      isEdit: true
    };
  },
  props: {
    userId: String
  },
  methods: {
    selectUp: function selectUp(e) {
      console.log(e);
      this.authority = e;
    },
    // 确定
    confirm: function confirm() {
      this.modal_loading = true;
      console.log(this.authority);
      console.log(JSON.stringify(this.authority));
      var dataValue = {
        account: this.account,
        password: this.password,
        name: this.name,
        permission: JSON.stringify(this.authority)
      };
      this.$emit('successAdd', dataValue);
    },
    // 取消
    cancel: function cancel() {
      this.hideModal();
    },
    showModal: function showModal(dataObj) {
      this.isEdit = true;
      this.modal = true;
      this.modal_loading = false;
      this.account = '';
      this.password = '';
      this.name = '';
      this.authority = '';
      this.data = '';
      this.newUserId = '' + '.' + new Date(); // 有值赋值

      if (Object.keys(dataObj).length !== 0) {
        this.account = dataObj.account;
        this.password = dataObj.password;
        this.name = dataObj.name;
      }
    },
    hideModal: function hideModal() {
      this.modal = false;
      this.modal_loading = false;
      this.account = '';
      this.password = '';
      this.name = '';
      this.authority = '';
      this.data = '';
    },
    // 复位
    restoration: function restoration() {
      this.modal_loading = false;
      this.account = '';
      this.password = '';
      this.name = '';
      this.authority = '';
      this.modal = false;
    }
  },
  mounted: function mounted() {},
  watch: {
    userId: function userId(e) {
      this.newUserId = e.split('|')[0] + '.' + new Date();
    }
  }
};
